package cn.thinking.system.unit.dao.impl;import java.util.List;import org.springframework.stereotype.Repository;import cn.thinking.common.basedao.BaseDao;import cn.thinking.org.system.unit.Unit;import cn.thinking.system.unit.dao.IUnitDao;@Repository(value = "cn.thinking.system.unit.dao.impl.UnitDaoImpl")public class UnitDaoImpl extends BaseDao implements IUnitDao {    @Override    public List<Unit> listAllParentUnit() {        return (List<Unit>) this.selectList("cn.thinking.org.system.unit.Unit.listAllParentUnit");    }    @Override    public List<Unit> listSubUnitByParentId(String parentId) {        return (List<Unit>) this.selectList("cn.thinking.org.system.unit.Unit.listSubUnitByParentId", parentId);    }    @Override    public Unit getUnitById(String orgId) {        return (Unit) this.selectOne("cn.thinking.org.system.unit.Unit.getUnitById", orgId);    }    @Override    public void insertUnit(Unit unit) {        this.insert("cn.thinking.org.system.unit.Unit.insertUnit", unit);    }    @Override    public void updateUnit(Unit unit) {        this.update("cn.thinking.org.system.unit.Unit.updateUnit", unit);    }    @Override    public void deleteUnitById(String orgId) {        this.delete("cn.thinking.org.system.unit.Unit.deleteUnitById", orgId);    }    @Override    public List<Unit> listAllSubUnit() {        return (List<Unit>) this.selectList("cn.thinking.org.system.unit.Unit.listAllSubUnit");    }}